import com.example.wealthwise.Models.Account;
import com.example.wealthwise.Models.Client;

import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.util.ArrayList;
import java.util.List;
import java.time.LocalDate;


    private Connection connection;

// Search clients and return full Client objects
public List<Client> searchClientsByPayeeAddress(String searchText) {
    List<Client> clients = new ArrayList<>();
    String query = "SELECT FirstName, LastName, PayeeAddress, Date FROM Clients WHERE PayeeAddress LIKE ?";
    try (PreparedStatement ps = connection.prepareStatement(query)) {
        ps.setString(1, "%" + searchText + "%");
        try (ResultSet rs = ps.executeQuery()) {
            while (rs.next()) {
                String fname = rs.getString("FirstName");
                String lname = rs.getString("LastName");
                String pAddr = rs.getString("PayeeAddress");
                LocalDate date = LocalDate.parse(rs.getString("Date"));

                // Wallet account
                Account wallet = null;
                try (PreparedStatement ps2 = connection.prepareStatement("SELECT * FROM WalletAccounts WHERE Owner = ?")) {
                    ps2.setString(1, fname + " " + lname);
                    try (ResultSet rs2 = ps2.executeQuery()) {
                        if (rs2.next()) {
                            wallet = new Account(rs2.getString("AccountNumber"), rs2.getDouble("Balance"));
                        }
                    }
                }

                // Savings account
                Account savings = null;
                try (PreparedStatement ps3 = connection.prepareStatement("SELECT * FROM SavingsAccounts WHERE Owner = ?")) {
                    ps3.setString(1, fname + " " + lname);
                    try (ResultSet rs3 = ps3.executeQuery()) {
                        if (rs3.next()) {
                            savings = new Account(rs3.getString("AccountNumber"), rs3.getDouble("Balance"));
                        }
                    }
                }

                clients.add(new Client(fname, lname, pAddr, wallet, savings, date));
            }
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
    return clients;
}

        // Deposit money into a clientâ€™s wallet
        public boolean depositToWallet(String payeeAddress, double amount) {
            String clientName = null;

            // Find client name
            String selectQuery = "SELECT FirstName, LastName FROM Clients WHERE PayeeAddress = ?";
            Connection connection;
            try (PreparedStatement ps = connection.prepareStatement(selectQuery)) {
                ps.setString(1, payeeAddress);
                try (ResultSet rs = ps.executeQuery()) {
                    if (rs.next()) {
                        clientName = rs.getString("FirstName") + " " + rs.getString("LastName");
                    }
                }
            } catch (Exception e) {
                e.printStackTrace();
                return false;
            }

            if (clientName == null) {
                System.err.println("Deposit failed: Payee not found");
                return false;
            }

            // Update balance
            String updateQuery = "UPDATE WalletAccounts SET Balance = Balance + ? WHERE Owner = ?";
            try (PreparedStatement ps = connection.prepareStatement(updateQuery)) {
                ps.setDouble(1, amount);
                ps.setString(2, clientName);
                int rowsAffected = ps.executeUpdate();
                return rowsAffected > 0;
            } catch (Exception e) {
                e.printStackTrace();
                return false;
            }
        }
